// landing.js
import React from "react";
import data from "../../CardData.js";
import "./Landing.css"; // Import the separate CSS file for LandingPage

const getTopRated = (mediaType) => {
  const filteredData = data.filter((item) => item.mediaType === mediaType);
  return filteredData.reduce(
    (top, current) => (current.stats.rating > top.stats.rating ? current : top),
    filteredData[0]
  );
};

function LandingPage() {
  const topRatedFilm = getTopRated("Film");
  const topRatedGame = getTopRated("Game");
  const topRatedBook = getTopRated("Book");
  const topRatedTVShow = getTopRated("TV Show");

  return (
    <div className="landing-page">
      <section className="top-section">
        <div className="horizontal-section">
          <div className="card">
            <p >
              Top Rated Film:&nbsp;
              {topRatedFilm.mediaTitle} - Rating:{" "}
              {topRatedFilm.stats.rating}
            </p>
            <img src={topRatedFilm.coverImage} alt={topRatedFilm.mediaTitle} />
          </div>
          <div className="card">
            <p>
              {topRatedGame.mediaTitle} (Game) - Rating:{" "}
              {topRatedGame.stats.rating}
            </p>
            <img src={topRatedGame.coverImage} alt={topRatedGame.mediaTitle} />
          </div>
        </div>
      </section>

      <section className="about-section">
        <h2>About Mashed Potatoes</h2>
        <p>
          Mashed Potatoes is a website dedicated to bringing you the top-rated
          media across film, gaming, books, and TV. Our mission is to provide
          insightful recommendations and ensure you always have something
          amazing to watch, play, or read. Join us in discovering the very best
          in entertainment!
        </p>
      </section>

      <section className="bottom-section">
        <div className="horizontal-section">
          <div className="card">
            <img src={topRatedBook.coverImage} alt={topRatedBook.mediaTitle} />
            <p>
              {topRatedBook.mediaTitle} (Book) - Rating:{" "}
              {topRatedBook.stats.rating}
            </p>
          </div>
          <div className="card">
            <img
              src={topRatedTVShow.coverImage}
              alt={topRatedTVShow.mediaTitle}
            />
            <p>
              {topRatedTVShow.mediaTitle} (TV Show) - Rating:{" "}
              {topRatedTVShow.stats.rating}
            </p>
          </div>
        </div>
      </section>
    </div>
  );
}

export default LandingPage;
